// import App from 'next/app'
import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";

function MyApp({ Component, pageProps }) {
  return (
    <>
      <Component {...pageProps} />
      <style jsx global>
        {`
          body {
            margin: 0;
            font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", "Roboto",
              "Oxygen", "Ubuntu", "Cantarell", "Fira Sans", "Droid Sans",
              "Helvetica Neue", sans-serif;
            -webkit-font-smoothing: antialiased;
            -moz-osx-font-smoothing: grayscale;
          }

          code {
            font-family: source-code-pro, Menlo, Monaco, Consolas, "Courier New",
              monospace;
          }

          html {
            font-size: 0.98vw !important;
            font-family: "mont-heavy";
          }

          .mt-views {
            padding-top: 0rem;
          }

          .w-80 {
            width: 80%;
          }
          .w-90 {
            width: 90%;
          }

          .sin-margin {
            margin-left: 0px !important;
            margin-right: 0px !important;
          }
          .pl-10 {
            padding-left: 6rem;
          }
          .title-fuxia {
            color: #ec608d;
            font-weight: 1000;
            font-size: 2.4rem;
            font-family: "mont-heavy";
            padding: 0 1rem;
            text-align: center;
          }

          .subtitle-dark {
            color: #575756;
            font-family: "mont-regular";
            font-size: 2rem;
          }

          .btn-yellow {
            background-color: #febf41;
            font-family: "omnes-bold";
            font-weight: 900;
            font-size: 1.4rem;
            color: #fff;
            padding: 1.5rem 0rem;
            margin: 2rem auto;
            width: 13rem;
            border-radius: 30px;
            text-align: center;
            line-height: 1.3rem;
            display: block;
          }
          .bg-fuxia {
            background: #ec608d !important;
          }

          .bg-verde {
            background-color: #c9dbc3 !important;
          }
          .bg-azul {
            background: #4b64a4 !important;
          }
          .bg-yellow {
            background-color: #febf41 !important;
          }

          .bg-gray {
            background-color: #575650 !important;
          }
          /* @media (min-width: 576px) {
  .box-web {
    max-width: 900px !important;
  }
}
@media (min-width: 900px) {
  .box-web {
    max-width: 992px !important;
  }
}
@media (min-width: 992px) {
  .box-web {
    max-width: 1200px !important;
  }
}
@media (min-width: 1200px) {
  .box-web {
    max-width: 1500px !important;
  }
} */
          .box-yesmom {
            max-width: 1800px;
            width: 100%;
            margin: auto;
          }
          .link-a {
            color: #575756 !important;
            font-family: "mont-regular";
            font-size: 1.1rem;
            text-decoration: none !important;
          }
          .link-a b {
            font-family: "mont-heavy";
          }

          .view-desktop {
            display: block !important;
            visibility: visible;
          }
          .view-mobile {
            display: none!important;
            visibility: hidden;
          }

          /* Efeccts hover focus  */
          .btn-efect {
            cursor: pointer;
            display: block;
            position: relative;
          }
          .btn-efect,
          .btn-efect::after {
            -webkit-transition: all 0.3s;
            -moz-transition: all 0.3s;
            -o-transition: all 0.3s;
            transition: all 0.3s;
          }
          .btn-efect::before,
          .btn-efect::after {
            background-color: #fff !important;
            content: "" !important;
            position: absolute;
            z-index: -1;
          }

          .btn-efect:hover {
            color: #ec608d;
            font-weight: 400;
            background-color: #fff !important;
            /* border: 1px solid #ec608d; */
          }

          .btn-efect::after {
            height: 100%;
            left: 0;
            top: 0;
            width: 0;
          }

          .btn-efect:hover:after {
            width: 100%;
          }

          /* hover-fuxia */
          .hover-fuxia {
            color: #fff !important;
            transition: all 0.5s;
            position: relative;
            text-decoration: none !important;
            cursor: pointer;
          }
          .hover-fuxia::before {
            content: "";
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            z-index: 999;
            background-color: rgba(214, 211, 211, 0.1);
            transition: all 0.3s;
          }
          .hover-fuxia:hover::before {
            opacity: 0;
            transform: scale(0.5, 0.5);
          }
          .hover-fuxia::after {
            content: "";
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            z-index: 6;
            opacity: 0;
            transition: all 0.3s;
            border: 1px solid #ec608d;
            transform: scale(1.2, 1.2);
            border-radius: 30px;
          }
          .hover-fuxia:hover::after {
            opacity: 1;
            transform: scale(1, 1);
          }

          /* hover-amarillo */
          .hover-amarillo {
            color: #fff !important;
            transition: all 0.7s;
            position: relative;
            text-decoration: none !important;
            cursor: pointer;
          }
          .hover-amarillo::before {
            content: "";
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            z-index: 999;
            background-color: rgba(214, 211, 211, 0.1);
            transition: all 0.3s;
          }
          .hover-amarillo:hover::before {
            opacity: 0;
            transform: scale(0.7, 0.7);
          }
          .hover-amarillo::after {
            content: "";
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            z-index: 6;
            opacity: 0;
            transition: all 0.3s;
            border: 1px solid #febf41;
            transform: scale(1.3, 1.3);
            border-radius: 30px;
          }
          .hover-amarillo:hover::after {
            opacity: 1;
            transform: scale(1, 1);
          }

          /* hover-verde */
          .hover-verde {
            color: #fff !important;
            transition: all 0.7s;
            position: relative;
            text-decoration: none !important;
            cursor: pointer;
          }
          .hover-verde::before {
            content: "";
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            z-index: 999;
            background-color: rgba(214, 211, 211, 0.1);
            transition: all 0.3s;
          }
          .hover-verde:hover::before {
            opacity: 0;
            transform: scale(0.7, 0.7);
          }
          .hover-verde::after {
            content: "";
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            z-index: 6;
            opacity: 0;
            transition: all 0.3s;
            border: 1px solid #c9dbc3;
            transform: scale(1.3, 1.3);
            border-radius: 30px;
          }
          .hover-verde:hover::after {
            opacity: 1;
            transform: scale(1, 1);
          }

          /* hover-azul */
          .hover-azul {
            color: #fff !important;
            transition: all 0.7s;
            position: relative;
            text-decoration: none !important;
            cursor: pointer;
          }
          .hover-azul::before {
            content: "";
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            z-index: 999;
            background-color: rgba(214, 211, 211, 0.1);
            transition: all 0.3s;
          }
          .hover-azul:hover::before {
            opacity: 0;
            transform: scale(0.7, 0.7);
          }
          .hover-azul::after {
            content: "";
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            z-index: 6;
            opacity: 0;
            transition: all 0.3s;
            border: 1px solid #4b64a4;
            transform: scale(1.3, 1.3);
            border-radius: 30px;
          }
          .hover-azul:hover::after {
            opacity: 1;
            transform: scale(1, 1);
          }

          .hover-efect-letter {
            text-decoration: none;
            position: relative;
            transition: all 300ms ease-in-out;
            cursor: pointer;
          }

          .hover-efect-letter:before,
          .hover-efect-letter:after {
            content: "";
            position: absolute;
            transition: all 250ms ease-in-out;
            pointer-events: none;
          }

          .hover-efect-letter:after {
            width: 100%;
            height: 1px;
            background: #575756;
            left: 0;
            bottom: -5px;
            transform: scale(0);
          }

          .hover-efect-letter:hover:after {
            transform: scale(1.1);
          }

          @media (max-width: 769px) {
            html {
