import React, { Fragment, useEffect, useState } from "react";
import Image from "next/image";
import { useRouter } from "next/router";
import AppLayout from "../../components/AppLayout";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faStar } from "@fortawesome/free-solid-svg-icons";

const CardBlogEspecific = ({ currentData }) => {
    const router = useRouter();
    console.log(currentData, "judith:)");

    // if(router.isFallback){
    //     return <div>CARGANDO...</div>
    // }

    return (
        <AppLayout>
            <div className="fade-in animated">
                <div className="box-banner-blog">
                    <Image
                        src="/image/onda-blanca.svg"
                        alt="ondas"
                        width={20}
                        height={50}
                        layout="responsive"
                        className="ondas"
                    />
                    <h4 className="text-title-blog">Detalles de blog</h4>
                    <Image
                        src="/image/onda-blanca.svg"
                        alt="ondas"
                        width={20}
                        height={50}
                        layout="responsive"
                        className="ondas"
                    />
                </div>
                <div className="box-blog-especifico">
                    <Image
                        src="/image/blog/nube1.png"
                        className="nube1 heartbeat"
                        width={70}
                        height={70}
                        layout="responsive"
                        alt="nube"
                    />
                    <Image
                        src="/image/blog/nube2.png"
                        className="nube2 heartbeat"
                        width={70}
                        height={70}
                        layout="responsive"
                        alt="nube"
                    />
                     <Image
                        src="/image/blog/nube3.png"
                        className="nube3 heartbeat"
                        width={70}
                        height={70}
                        layout="responsive"
                        alt="nube"
                    />
                    <div className="box-autor-blog">
                        <figure>
                            <img src={imgAutor} alt="" className="w-100" />
                        </figure>
                        <div className="box-autor-text-blog">
                            <h5 className="text-autor">YesMom</h5>
                            <h5 className="text-fecha">20/04/2020</h5>
                        </div>
                    </div>
                    <div className="box-articulo-blog">
                        <figure className="box-articulo-img-principal-blog">
                            <img src={imgPrincipal} alt="" className="w-100" />
                        </figure>
                        <div className="box-articulo-title-blog">
                            <FontAwesomeIcon
                                icon={faStar}
                                className="star-blog heartbeat"
                            ></FontAwesomeIcon>
                            <h2> Tips para tu baby shower virtual</h2>
                            <FontAwesomeIcon
                                icon={faStar}
                                className="star-blog heartbeat"
                            ></FontAwesomeIcon>
                        </div>
                        <JoditEditor
                            config={config}
                            value={contenido}
                            name="contenido"
                        // onBlur={handleBlurAreaChange}
                        />
                    </div>
                </div>
            </div>
        </AppLayout>
    );
};

export async function getStaticPaths() {
    // Call an external API endpoint to get posts
    const res = await fetch("http://localhost:5000/getBlogAll/user?limit=all");
    const blogs = await res.json();
    console.log(blogs.length, "ojitoa");
    // Get the paths we want to pre-render based on posts
    const paths = blogs.map((blog) => ({
        params: { blog: String(blog.blog._id) },
    }));

    // We'll pre-render only these paths at build time.
    // { fallback: false } means other routes should 404.
    return { paths, fallback: false };
}

export async function getStaticProps({ params }) {
    // Call an external API endpoint to get posts.
    // You can use any data fetching library
    const res = await fetch(`http://localhost:5000/getBlog/${params.blog}`);
    const currentData = await res.json();

    // By returning { props: posts }, the Blog component
    // will receive `posts` as a prop at build time
    return {
        props: {
            currentData: currentData[0],
        },
    };
}

export default CardBlogEspecific;
