import React, { Fragment, useEffect, useState } from "react";
import { Card, CardDeck, Container } from "react-bootstrap";
// import CardBlog from "./CardBlog";
import AppLayout from "../../components/AppLayout";
import Image from "next/image";
import Link from "next/link";
import CardBlog from "../../components/CardBlog";
import Head from "next/head";

const Blog = ({ currentData }) => {
  return (
    <AppLayout>
      <Head>
        <title>YesMom - Blog</title>
        <meta name="description" content="Visita nuestro blog ..."></meta>
        <meta property="og:type" content="website" />
        <meta property="og:title" content="YesMom - Blog" />
        <meta property="og:description" content="Visita nuestro Blog ..." />
        <meta
          property="og:image"
          itemprop="image"
          content="https://yesmom.vercel.app/image/blog/blog.png"
        />
        <meta property="og:site_name" content="La Ganga" />
        {/* <meta property="og:url" content={`${user.id}`} />  */}
        <meta name="twitter:card" content="summary" />
        <meta name="twitter:title" content="YesMom - Blog" />
        <meta name="twitter:description" content="Visitanuestro Blog ..." />
        <meta
          name="twitter:image"
          content="https://yesmom.vercel.app/image/blog/blog.png"
        />
        <meta name="twitter:site" content="@JudithCristinaQ" />
        <meta name="twitter:creator" content="@JudithCristinaQ" />
      </Head>
      <div className="fade-in animated">
        <div className="box-banner-blog">
          <div className="ondas">
            <Image
              src="/image/onda-blanca.svg"
              alt="ondas"
              width={70}
              height={20}
            />
          </div>
          <h4 className="text-title-blog">Blog</h4>
          <div className="ondas">
            <Image
              src="/image/onda-blanca.svg"
              alt="ondas"
              width={70}
              height={20}
            />
          </div>
        </div>
        <div className="box-blog-general">
          <h4 className="subtitle-dark-blog">
            En este espacio compartiremos algunos tips que te servirán para
            cuidar y engreir mucho más a tu bebé
          </h4>
          <div className="box-card-group">
            <Container>
              <CardDeck style={{justifyContent:"center"}}>
                {currentData.map((cardBlog) => (
                  <CardBlog blog={cardBlog} key={cardBlog.blog._id} />
                ))}
              </CardDeck>
            </Container>
          </div>
        </div>
        <div className="box-green-blog">
          {/* <div className="w-100 box-green-blog-img-fondo"> */}
            {/* <Image
              src="/image/blog/fondo-verde.png"
              width={1920}
              height={800}
              layout="responsive"
              alt="Picture of the author"
            /> */}
          {/* </div> */}
          <h6 className="text-bottom-blog ">
            Para el mundo eres una mamá,<br></br>
            <b>para tus hijos eres el mundo</b>
          </h6>
        </div>
      </div>
      <style jsx>
        {`
          .box-banner-blog {
            height: 25rem;
            background: url(../../image/blog/blog.png) no-repeat center;
            background-size: cover;
            display: flex;
            justify-content: center;
            align-items: center;
            padding-top: 5rem;
          }

          .text-title-blog {
            font-family: "Clicker Script", cursive;
            font-size: 2.5rem;
            margin: 0rem 1rem 0rem;
            color: #fff;
            text-align: center;
          }

          .subtitle-dark-blog {
            color: #575756;
            font-family: "mont-regular";
            font-size: 1.7rem;
            text-align: center;
            margin: 0rem 15rem;
            margin-bottom: -12rem;
            margin-top: 3rem;
          }
          .box-card-group {
            position: relative;
            top: 16rem;
            z-index: 10;
          }


          .box-green-blog {
            position: relative;
            background: url(../../../image/fondo-verde-somos.png);
            height: 28rem;
            background-position: center;
            background-size: cover;
            padding-top: 5rem;
            padding-bottom: 5rem;
          }

          .text-bottom-blog {
            color: #abc0a3;
            font-family: "mont-regular";
            font-size: 1.7rem;
            text-align: center;
            /* margin: 3rem 20rem; */
            position: absolute;
            bottom: 16%;
            left: 50%;
            transform: translateX(-50%);
          }

          .text-bottom-blog b {
            font-family: "mont-semibold";
          }
          .font-mont-regular {
            font-family: "mont-regular";
          }

          .box-blog-especifico {
            width: 100%;
            display: flex;
            padding: 4rem 9rem;
            font-family: "mont-regular";
          }

          .box-autor-blog {
            display: flex;
            flex-direction: row;
            justify-content: left;
            align-items: center;
            height: 7rem;
            width: 20%;
          }
          .box-autor-text-blog {
            padding-left: 1rem;
          }
          .box-articulo-blog {
            width: 80%;
          }
          .text-autor {
            color: #575756;
            font-style: normal;
            font-weight: bold;
            font-size: 1.2rem;
            font-family: "mont-heavy";
          }
          .text-fecha {
            color: #556ea1;
            font-style: normal;
            font-weight: normal;
            font-size: 1rem;
          }
          .box-articulo-title-blog {
            padding: 0rem 2rem;
            display: flex;
            flex-direction: row;
            justify-content: center;
            align-items: center;
          }
          .box-articulo-title-blog h2 {
            font-family: "mont-semibold";
            font-style: normal;
            font-weight: bold;
            text-align: center;
            color: #575756;
            margin: 2rem 0rem;
          }
          .star-blog {
            color: #febf41;
